<?php class ModelPaymentQrcodeweipay extends Model {  	public function getMethod($address) {		$this->load->language('payment/qrcodeweipay');		if ($this->config->get('qrcodeweipay_status')) {			$status = TRUE;		} else {			$status = FALSE;		}		$method_data = array();		if ($status) {  			$method_data = array( 				'code'         => 'qrcodeweipay',				'title'      => $this->language->get('text_title'),				'terms'      => '',				'sort_order' => $this->config->get('qrcodeweipay_sort_order')			);		}		return $method_data;	}}?>